package chap02;

public class charEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		char ch = 65;  
		short ch1 = 65;
		
		System.out.println(ch);    //char타입은 저장된 숫자에 해당하는 유니코드 문자를 출력
		System.out.println((int)ch); //숫자로 출력하고 싶으면 형변환.
		System.out.println(ch1);
	}

}
